import React, { Component } from "react";
import { Card, CardTitle, CardImg, CardImgOverlay } from "reactstrap";

class DishdetailComponent extends Component {
  render() {
    var comment;
    const select = this.props.dish;
    console.log(select);
    for (let key in select) {
      if (key == "name") {
        var selectedName = select[key];
      }
      if (key == "image") {
        var selectedImage = select[key];
      }
      if (key == "id") {
        var selectedId = select[key];
      }

      if (key == "comments") {
        var selectedcomment = select[key];
        comment = selectedcomment.map(comment => {
          return <p key={selectedId}>{comment}</p>;
        });
      }
    }

    return (
      <div className="row">
        <div className="col-12 col-md-5 m-1">
          <Card>
            <CardImg width="100%" src={selectedImage} />
            <CardImgOverlay>
              <CardTitle>{selectedName}</CardTitle>
            </CardImgOverlay>
          </Card>
        </div>
        <div className="col-12 col-md-5 m-1">{comment}</div>
      </div>
    );
  }
}
export default DishdetailComponent;
