import React, { Component } from "react";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  Breadcrumb,
  BreadcrumbItem
} from "reactstrap";
import { Link } from "react-router-dom";

class DishdetailComponent extends Component {
  renderDish(dish) {
    if (dish.selectedDish != null) {
      console.log(dish.selectedDish.id);
      return (
        <Card>
          <CardImg
            key={dish.selectedDish.id}
            top
            src={dish.selectedDish.image}
            alt={dish.selectedDish.name}
          />
          <CardBody>
            <CardTitle>{dish.selectedDish.name}</CardTitle>
            <CardText>{dish.selectedDish.description}</CardText>
          </CardBody>
        </Card>
      );
    } else return <div></div>;
  }

  render() {
    if (this.props.selectedDish != null) {
      var comments = this.props.selectedDish.comments.map(comment => {
        console.log(comment);
        return (
          <div key={comment.id}>
            <p>{comment.comment}</p>
            <p>
              --{comment.author}, {comment.date}
            </p>
          </div>
        );
      });
    } else return <div></div>;
    return (
      <div className="container">
        <div className="row">
          <div className="col-12 col-md-5 m-1">
            {this.renderDish(this.props)}
          </div>
          <div className="col-12 col-md-5 m-1">
            <h4>Comments</h4>
            <div>{comments}</div>
          </div>
        </div>
      </div>
    );
  }
}
export default DishdetailComponent;
